<?php/** * Основной шаблон * ======================= * $text - текст */?><!-- Шаблон страницы регистрации пользователя --><div class="registration-container">    <!-- Свединия о заполнении полей -->    <p class="red"><?=$response?></p>    <!-- Форма регистрации -->    <form id="registration" method="post">        <h1>Регистрация</h1>        <div class="group">            <label for="username">Как Вас зовут? </label>            <input type="text" id="username" name="name" placeholder="Имя" value="<?=$user_name?>" required/><div id="feedback"></div>        </div>        <div class="group">            <label for="login">Введите логин </label>            <input type="text" id="login" name="login" placeholder="Логин" value="<?=$user_login?>" required/><div id="feedback"></div>        </div>        <div class="group">            <label for="email">Введите email </label>            <input type="email" id="email" name="email" placeholder="Email" value="<?=$user_email?>" required/><div id="feedback"></div>        </div>        <div class="group">            <label for="password">Введите пароль </label>            <input type="password" id="password" placeholder="Пароль" name="password" required/><div id="feedback"></div>        </div>        <div class="group" align="center">            <button class="btn btn-dark">Регистрация</button>        </div>    </form></div>