<?php/** * Основной шаблон * ======================= * $text - текст */?><!-- Страница конкретной задачи --><div class="task-item-container">    <!-- Ответ о действиях на странице -->    <?=$response?>    <!-- Данные задачи -->    <h1 class="card-title"><?=$task_name?></h1>    <p>Описание: </p>    <p class="task_description"><?=$task_description?></p>    <p>Ответсвенный: <?=$user_name?></p>    <p>Создана: <?=date('Y-m-d H:i:s', $created_at)?></p>    <p>Срок исполнения: <?=date('Y-m-d H:i', $dead_line)?></p>    <p>Статус: <?=$status_name?></p>    <!-- Кнопки действий с заадчей -->    <div class="admin-buttons">        <?        // Кнопка удаления задачи            if ($_SESSION['user']['login'] == 'admin' || $_SESSION['user']['login'] == "$user_login" && $user_login != null){            ?>            <a class="btn btn-danger" href="?act=one&delete=on&id_task=<?=$task_id?>" role="button">Удалить</a>        <?}        // Если задача редактируется, убираем кнопку "Редактировать"        if (!isset($_GET['edit'])) {            // Редактирование текста задачи            if ($_SESSION['user']['login'] == 'admin' || $_SESSION['user']['login'] == "$user_login"  && $user_login != null) {                ?>                <a class="btn btn-primary" href="index.php?c=page&act=create&id_task=<?= $task_id ?>&edit=on"                   role="button">Редактировать</a>                <?            }        }        // Кнопка смены статуса задачи        if ($_SESSION['user']['login'] == 'admin' && $id_status == 2 || $_SESSION['user']['login'] == "$user_login"            && $id_status == 2 && $user_login != null){            ?>            <a class="btn btn-success" href="?act=one&completed=1&id_task=<?=$task_id?>" role="button">Выполнена</a>        <?}        ?>        <?        if ($_SESSION['user']['login'] == 'admin' && $id_status == 1 || $_SESSION['user']['login'] == "$user_login"        && $id_status == 1 && $user_login != null){        ?>        <a class="btn btn-warning" href="?act=one&completed=2&id_task=<?=$task_id?>" role="button">Не выполнена</a>        <?}        ?>    </div></div>